import React, { useEffect, useState } from "react";
import "./NavBar.module.css";
import logo from "./assets/SchorTax.png";

const Navbar = () => {
  const [scrolled, setScrolled] = useState(false);

  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 1) {
        setScrolled(true);
      } else {
        setScrolled(false);
      }
    };
    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);

  return (
    <nav className={`navbar ${scrolled ? "shrink" : ""}`}>
      <a className="navbarTitle" href="#top">
        <img src={logo} alt="Schor Tax & Consulting" className={`navbarLogo ${scrolled ? "shrink" : ""}`} />
      </a>
      <div className="navbarLinks">
        <a className="navbarLink" href="#services">
          Services
        </a>
        <a className="navbarLink" href="#packages">
          Packages/Pricing
        </a>
      </div>
    </nav>
  );
};

export default Navbar;
